		return array;
	}else{
		var ny = array.sort(function(a, b){
			if (a.dataElement < b.dataElement){
				return -1;
			}
			if (a.dataElement > b.dataElement){
				return 1;
			}
			return 0;
		});

		return ny;
	}
}

