import React, { Component } from 'react';
/**
	Lagre doktorene i objekter, hvor de har en array av sine reports?
	Lage en objekt for reports, som har dato og content i seg?

	Lage en funksjon som generere en tr for navn og en for dato, 
	disse skal kunne sorteres. 
**/
class SearchBar extends Component{
	constructor(props){
		super(props);
		this.state = {
			textValue: "",
			asc: false,
			dec: false,
			dummyData: [],
			curr: []
		};

		this.onChange = this.onChange.bind(this);
		this.setInputState = this.setInputState.bind(this);
	}

	createEntry(name, time){
		return {doctor_name: name, logged:time};
	}

	setInputState(input){
		this.state.dummyData.push(input);


	}
	componentDidMount(){
		for(var i = 0; i < 10; i++){
			fetch('https://randomuser.me/api/')
			.then(reply => reply.json())
			.then(result => {
				console.log(result);
				var name = result.results[0].name.last;
				var time = result.results[0].registered.date;
				var entry = this.createEntry(name, time);
				this.setInputState(entry);
			});
		}

	}
	onChange(event){
		var sum = [];
		console.log("Hei");
		for(var i = 0; i < this.state.dummyData.length; i++){
			if(this.state.dummyData[i].doctor_name.startsWith(event.target.value)){
				console.log(this.state.dummyData[i].doctor_name + " hadde " + event.target.value);
				sum.push(this.state.dummyData[i]);
			}
		}
		this.setState({curr: sum, textValue: sum[0].doctor_name});


	}

	sortByName(){
		var temp = this.state.dummyData;
		if(!this.state.asc){
			var ny = temp.sort(function(a, b){
				return a.doctor_name - b.doctor_name;
			});
			this.setState({textValue: ny, asc: true, dec:false});
		}else if(!this.state.dec){
			var ny = temp.sort(function(a, b){
				return a.doctor_name - b.doctor_name;
			}).reverse();
			this.setState({textValue: ny, asc:false, dec:true});
		}
	}

	sortByDate(){
		console.log("Sorted by date");
	}


	render(){
		var ul = document.createElement('ul');

		if(this.state.dummyData.length > 0){
			console.log("Kj√∏r da");
			for(var i = 0; i < this.state.dummyData.length; i++){
				var li = document.createElement('li');
				li.innerHTML = this.state.dummyData[i].doctor_name;
				ul.appendChild(li);
			}
		} 
		console.log(ul);
		var value = null;
		if(this.state.curr.length < 1){
			value = this.state.dummyData.doctor_name;
		}else{
			value = this.state.curr.doctor_name;
		}
		return(
			<div>
			<button onClick={this.sortByName.bind(this)}>Sort by name</button>
			<button onClick={this.sortByDate.bind(this)}>Sort by date</button>
			<input type="text" onChange={this.onChange.bind(this)} />
			<p>{this.state.textValue.doctor_name}</p>
			<table>
				<tr>
				</tr>
				<tr>
				</tr>
			</table>
			</div>
		);
	}

}

export default SearchBar;
