import React, { Component } from 'react';

class SearchBar extends Component{
	constructor(props){
		super(props);
		this.state = {
			textValue: "",
			asc: false,
			dec: false,
			dummyData: ["Bob", "Lars", "Arne"]
		};

		this.onChange = this.onChange.bind(this);
	}

	onChange(event){
		var sum = "";
		for(var i = 0; i < this.state.dummyData.length; i++){
			if(this.state.dummyData[i].contains(event.target.value)){
				sum += this.state.dummyData[i] + " ";
			}
		}
	}

	sortByName(){
		var temp = this.state.dummyData;
		if(!this.state.asc){
			var ny = temp.sort();
			this.setState({textValue: ny, asc: true, dec:false});
		}else if(!this.state.dec){
			var ny = temp.sort().reverse();
			this.setState({textValue: ny, asc:false, dec:true});
		}
	}

	sortByDate(){
		console.log("Sorted by date");
	}


	render(){
		return(
			<div>
			<button onClick={this.sortByName.bind(this)}>Sort by name</button>
			<button onClick={this.sortByDate.bind(this)}>Sort by date</button>
			<input type="text" onChange={this.onChange.bind(this)} value={this.state.textValue} />
			<p>{this.state.textValue}</p>
			</div>
		);
	}

}

export default SearchBar;
